    private void TxtAngkaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TxtAngkaKeyTyped
        // TODO add your handling code here:                                 
        char c = evt.getKeyChar();
        if (!Character.isDigit(c) && c != KeyEvent.VK_BACK_SPACE && c != KeyEvent.VK_DELETE) {
            evt.consume();
            JOptionPane.showMessageDialog(this, "Masukkan hanya angka!", "Input Error", JOptionPane.ERROR_MESSAGE);

}

    }//GEN-LAST:event_TxtAngkaKeyTyped

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        try {
        int angka = Integer.parseInt(TxtAngka.getText());
        String hasil = "";

        // Cek genap atau ganjil
        if (angka % 2 == 0) {
            hasil += angka + " adalah bilangan GENAP.\n";
        } else {
            hasil += angka + " adalah bilangan GANJIL.\n";
        }

        // Cek bilangan prima
        if (angka < 2) {
            hasil += angka + " bukan bilangan PRIMA.";
        } else {
            boolean prima = true;
            for (int i = 2; i <= Math.sqrt(angka); i++) {
                if (angka % i == 0) {
                    prima = false;
                    break;
                }
            }
            hasil += prima ? angka + " adalah bilangan PRIMA." : angka + " bukan bilangan PRIMA.";
            TxtAngka.requestFocus();
            LblHasil.setText(hasil);
        }

        // ðŸ”¹ Tampilkan hasil dalam pop-up
        JOptionPane.showMessageDialog(this, hasil, "Hasil Pemeriksaan", JOptionPane.INFORMATION_MESSAGE);

    } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, "Masukan Angka Dengan Benar.", "Error", JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_jButton1ActionPerformed

